"use strict";
jQuery(window).on("load", function() {

    setTimeout(function() {
        jQuery(".oc_error").fadeOut()
    }, 3000);

    setTimeout(function() {
        jQuery(".oc_success").fadeOut()
    }, 3000);

    jQuery("#oc-template-cancel").click(function(){
         window.location.href = "admin.php?page=newsletter-template-listing";
    });

    jQuery("#oc_selectall").click(function () {
		 jQuery('.oc_checkbox-class').attr('checked', this.checked);
	});


});

function oc_checkbox_validation(){
  var oc_checkval = jQuery("input[class=oc_checkbox-class]:checked").length;
  if(oc_checkval == 0) {
    alert("You must check at least one checkbox.");
    return false;
  }
}